mapper.select:
select


from platform.shop_rack where 1=1


	and area=#{area}



	and shopId=#{shopid}



	and groupId=#{groupid}



	and rackNo=#{rackNo}

select
from platform.shop_rack where 1 = 1
and shopId = #{shopid,jdbcType=VARCHAR}

mapper.insert:
insert into shop_rack (id, shopId, shopName,
groupId, groupName, disType,
imgUrl, rackNo, area
)
values (#{id,jdbcType=BIGINT}, #{shopid,jdbcType=VARCHAR},
#{shopname,jdbcType=VARCHAR},
#{groupid,jdbcType=VARCHAR}, #{groupname,jdbcType=VARCHAR}, #{distype,jdbcType=VARCHAR},
#{imgurl,jdbcType=VARCHAR}, #{rackno,jdbcType=VARCHAR},
#{area,jdbcType=DOUBLE}
)
