mapper.select:
SELECT sap_shopId as id, SName as text FROM dw.`d_bravo_shop` WHERE 1=1
and AreaName IN ('广东', '北京', '华东', '华西','福建','四川','河北','东北')
and province = #{province}

select DISTINCT areaMans from platform.`d_bravo_shop`

    Congou=1

        and AreaName = #{largeArea}

select DISTINCT areaMans from platform.`d_bravo_shop`

    Congou=1

        and AreaName = #{largeArea}

select sap_shopId as id, SName as text
from platform.d_bravo_shop

    Congou=1

        and AreaName = #{largeArea}


        and AreaMans = #{areaMans}


        and province = #{province}


        and city = #{city}


order by AreaName,AreaMans;

select m.*,(select p.name from platform.menu_forfront p where p.id = m.pid) as pName
from platform.menu_forfront m


        and m.id = #{id}


        and m.`name` like concat('%',#{name},'%')


        and m.`pid` =#{pid} or m.id=#{pid}




        order by ${sidx} ${order}


        order by m.id asc



    limit #{offset}, #{limit}

select count(*) from platform.menu_forfront m


        and id = #{id}


        and `name` like concat('%',#{name},'%')


        and m.`pid` =#{pid} or m.id=#{pid}

select m.*,(select p.name from platform.menu_forfront p where p.id = m.pid) as pName
from platform.menu_forfront m where m.`pid` =#{value}
order by m.sort asc

select m.`id`,m.`sort`
from platform.menu_forfront m where m.`id` =#{value}
order by m.sort asc
